import React, { Component } from "react";
import AES from 'crypto-js/aes'

class Sidebar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            status: "LOADING",
            events: ""
        };
        this.getAllEvents = this.getAllEvents.bind(this);
        this.processResponse = this.processResponse.bind(this);
    }

    getAllEvents() {
        const httpOptions = {
            headers: { 'x-teamengine-test': "YfKFyOBnLBvudfn" }
        };
        const url = `https://projects.teamengine.com/calendar/events`;
        fetch(url, httpOptions).then(response => {return response.text()});
    }

    processResponse(response) {
        if (response.ok) {
            return JSON.parse(AES.decrypt(response, 'teamengine-key').toString());
        }
        throw response;
    }

    componentDidMount() {
        // when data is retrieved we update the state
        // this will cause the component to re-render
        this.getAllEvents()
            .then(events => {
                this.setState({
                    status: "LOADED",
                    events: events
                });
            })
            .catch(() => {
                this.setState({
                    status: "ERROR"
                });
            });
    }

    render() {
        console.log(this.state.events);
        // depending on the state we either generate
        // useful message to the user or show the list
        // of returned dishes
        let events = null;
        switch (this.state.status) {
            case "LOADING":
                events = <em>Loading...</em>;
                break;
            case "LOADED":
                events = this.state.events.map(event => (
                    console.log(event)
                ));
                break;
            default:
                events = <b>Failed to load data, please try again</b>;
                break;
        }
        return (
            <div className="events">
                {events}
            </div>
        );
    }
}

export default Sidebar;
