import React, { Component } from "react";
import AES from 'crypto-js/aes'
import enc from 'crypto-js/enc-utf8'
import Fetcher from "./fetchfuncs"

class Sidebar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            status: "LOADING",
            events: ""
        };
        this.fetcher = new Fetcher();
        this.getAllEvents = this.getAllEvents.bind(this);
        this.processResponse = this.processResponse.bind(this);
    }

    processResponse(response) {
            return JSON.parse(AES.decrypt(response, 'teamengine-key').toString(enc));

    }

    componentDidMount() {
        // when data is retrieved we update the state
        // this will cause the component to re-render
        this.fetcher.getAllEvents()
            .then(events => {
                console.log(AES.decrypt(events, "teamengine-key").toString(enc))
                events = this.processResponse(events).events;
                this.setState({
                    status: "LOADED",
                    events: events
                });
            })
            .catch(() => {
                this.setState({
                    status: "ERROR"
                });
            });
    }

    render() {
        console.log(this.state.events);
        // depending on the state we either generate
        // useful message to the user or show the list
        // of returned dishes
        let events = null;
        switch (this.state.status) {
            case "LOADING":
                events = <em>Loading...</em>;
                break;
            case "LOADED":
                events = this.state.events.map(event => (
                    console.log(event)
                ));
                break;
            default:
                events = <b>Failed to load data, please try again</b>;
                break;
        }
        return (
            <div className="events">
                {events}
            </div>
        );
    }
}

export default Sidebar;
