import React, { Component } from "react";
import Model from "./EventModel";

class Sidebar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            status: "LOADING"
        };
    }

    getAllEvents() {
        const httpOptions = {
            headers: { 'x-teamengine-test': "YfKFyOBnLBvudfn" }
        };
        const url = `https://projects.teamengine.com/calendar/events`;
        return fetch(url, httpOptions).then(this.processResponse);
    }

    processResponse(response) {
        var CryptoJS = require("crypto-js");
        if (response.ok) {
            return CryptoJS.AES.decrypt(response.toString(), 'teamengine-key');
        }
        throw response;
    }

    componentDidMount() {
        var CryptoJS = require("crypto-js");
        // when data is retrieved we update the state
        // this will cause the component to re-render
        getAllEvents()
            .then(events => {
                this.setState({
                    status: "LOADED",
                    events: JSON.parse(events.toString(CryptoJS.enc.Utf8))
                });
            })
            .catch(() => {
                this.setState({
                    status: "ERROR"
                });
            });
    }

    render() {
        return (
            <div>
                <p>The sidebar!</p>
            </div>
        );
    }
}

export default Sidebar;
