import React, { Component } from "react";
import modelInstance from "./model";

class Sidebar extends Component {
  constructor(props){
    super(props);
    this.state = {
        status: "LOADING"
      };
  }

  componentDidMount() {
    var CryptoJS = require("crypto-js");

    // when data is retrieved we update the state
    // this will cause the component to re-render
    modelInstance
      .getAllEvents()
      .then(events => {
        this.setState({
          status: "LOADED",
          events: JSON.parse(events.toString(CryptoJS.enc.Utf8))
        });
      })
      .catch(() => {
        this.setState({
          status: "ERROR"
        });
      });
  }

  render() {
    return (
      <div>
        <p>The sidebar!</p>
      </div>
    );
  }
}

export default Sidebar;
